import getUser from '../actions/getUser'
import Book from '../Components/Book'
const OwnedBooks= ()=>{
    const [Books,setBooks] = useState([]);
    const getBooks = async ()=>{
        const username =  getUser();
        await ownedbooks(username).then((res)=>{
            if(res.status===200)
            {setBooks(res.data);
            console.log(Books)
           }
        })
           
    }
   
    useEffect(()=>{
      
        getBooks();
        
        },[]);
    
     return (<div>
