import React,{useState,useEffect} from 'react'
import {bookById} from '../Api-requests/bookRequests'
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
import image from "../assets/images/uploadbk.jpg"

const useStyles = makeStyles({
    root: {
      maxWidth: 345,
    },
    media: {
      height: 140,
    },
  });
const ExpandedBook = (props)=>{
    const [book,setBook] = useState({})
    useEffect( ()=>{
        const bookid = props.match.params.bookid;
        console.log(bookid);
        async function getBook(){
            await bookById(bookid).then((res)=>{
                setBook(res.data)
               })
        }
        getBook()
    },[])
   
      const classes = useStyles();
return(
    <div>
    <Card className={classes.root}>
        <CardMedia
          className={classes.media}
          image={image}
        //   image={book.imagepath}
        />
        <CardContent>
          <Typography gutterBottom variant="h4" color="primary" component="h2">
            {book.bookname}
            </Typography>
            <Typography gutterBottom variant="h5" color="primary" component="h3">
            {book.author}
            </Typography>
            <Typography gutterBottom variant="h5" color="primary" component="h3">
            {book.subject}
            </Typography>
            <Typography gutterBottom variant="h5" color="secondary" component="h3">
            {book.price}
            </Typography>
        </CardContent>
    </Card>
        
    </div>
)
}
export default ExpandedBook;
