import { addtocart } from "../Api-requests/userRequests"
const addToCart = (props)=>{
    addtocart(props.bookid).then((res)=>{
         
        if(res.status===200)
        {
            alert("Book added to cart succesfully")
        }
        else
        {
            alert("Couldnt add book! Try again later")
        }
    })
}
export default addToCart;
