import React,{useState,useEffect, Fragment} from 'react'
import {bookById,getOwnerInfo} from '../Api-requests/bookRequests'
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import StarsIcon from '@material-ui/icons/Stars';
import Typography from '@material-ui/core/Typography';
import getUser from '../actions/getUser'
import InnerNavbar from '../Components/InnerNavbar'
import handleAddToCart from "../actions/handleAddToCart"
import deleteFromCart from "../actions/deleteFromCart"
import {isBookofUser,inCartofUser} from "../Api-requests/bookRequests"
const ExpandedBook = (props)=>{
    const [book,setBook] = useState({
      author:'',
      bookname:'',
      price:'',
      subject:'',
      id:''
    })
    const [owner,setOwner] = useState({})
    const [user,setUser] = useState(' ')
    const [inCart,setIncart] = useState(false)
    const [isUsers,setIsusers] = useState(false)
    let bookid;
    useEffect( ()=>{
      const username  = getUser();
      setUser(username);
       bookid = props.match.params.bookid;
        async function getBook(){
            await getOwnerInfo(bookid).then((res)=>{
                setOwner(res.data[0])
                console.log(owner)
                console.log("After owner")
            })
            //console.log(process.env.PUBLIC_URL +'/images/uploads/'+ bookid + '.PNG')
           await  bookById(bookid).then((res)=>{             
                setBook(res.data)
                
               });        
        } 
        getBook();
        inCartofUser(props.id)
        .then((res)=>{
          if(res.status === 200){
          setIncart(res.data);
          }
        })
        isBookofUser(props.id)
        .then((res)=>{
          if(res.status === 200){
          setIsusers(res.data);
          }
        });
        
        
    },[])
   
    const handleCart =()=>{
        
      console.log(props.id)
      if(!inCart)
       handleAddToCart(props.id)
       else
       deleteFromCart(props.id)
    }
      
return(
 <Fragment>
    <InnerNavbar username={user}/>
    <div className="expandedBook">
    <img src={process.env.PUBLIC_URL+'/images/uploads/'+book.imagepath }></img>
          <Typography gutterBottom variant="h4" color="secondary" component="h2">
            {book.bookname}
            </Typography>
            {isUsers? <StarsIcon/> : null}
            <Typography gutterBottom variant="body1" color="primary" component="h3">
            Author:{book.author}
            </Typography>
            <Typography gutterBottom variant="body1" color="primary" component="h3">
            Subject:{book.subject}
            </Typography>
            <Typography gutterBottom variant="body1" color="primary" component="h3">
            Owner:{owner.username}
            
            </Typography>
            <Typography gutterBottom variant="body1" color="primary" component="h3">
            Contact Info:{owner.email}
            {/* Contact info: nithyamanoj.ms@gmail.com */}
            </Typography>
            <Typography gutterBottom variant="body2" color="primary" component="h5">
            Price: {book.price}
            </Typography>
            {inCart?
        <IconButton color="primary"
        aria-label="add to cart" onClick={handleCart}>
        <FavoriteIcon/>
        </IconButton>
        :
            <Button
            onClick={()=>{handleCart} }
          variant="contained"
          color="default"
          startIcon={<AddIcon/>}
          
      >Add to Cart!</Button>}
        
    </div>
 </Fragment>
   
)
}
export default ExpandedBook;
