import getUser from '../actions/getUser'
import Book from '../Components/Book'
const OwnedBooks= ()=>{
    const [Books,setBooks] = useState([]);
    const getBooks = async ()=>{
        const username =  getUser();
        const res =await ownedbooks(username);
            if(res.status===200)
             {setBooks(res.data);
             console.log(Books)
            }
    }
   
    useEffect(()=>{
      
        getBooks();
        
        },[]);
    
     return (<div>
        <h2>Your books</h2>
        <TableBox Books={Books}/>
    </div>)
}
